class SingleBook extends Component {

  state = {
    selected: flase,
  };

  handleCardClick = () => {
    this.setState((prevState) => ({
      selected: !prevState.selected,
    }));
  };

  render() {
    return (
      <Col xs="6" md="4" lg="3">
        <Card className={`h-100 ${this.state.selected ? 'border-danger border-2' : ''}`}>
          <Card.Img variant="top" src={this.props.book.img} style={{ objectFit: "cover" }} />
          <Card.Body className="d-flex flex-column justify-content-between">
            <Card.Title>{this.props.book.title}</Card.Title>
            <Card.Text className="bg-dark text-light rounded-5">
              <b> {this.props.book.price}$</b>
            </Card.Text>
          </Card.Body>
        </Card>
      </Col>
    );
  }
}
export default SingleBook;
