import { Component } from "react";

class CommentArea extends Component {
  render() {

    return (
      <div className={`rounded-3 bg-light mt-1 ${this.props.selected ? "d-block" : "d-none"}`}>
        <p>Comments: {this.props.elementId}</p>
      </div>
    );
  }
}
export default CommentArea;
